/// 蓝牙Errno
public enum OpenAPIBluetoothErrno: OpenAPIErrnoProtocol {
    // 蓝牙适配器未初始化
    case adapterUninitialized
    // 当前蓝牙适配器不可用
    case adapterUnavailable
    // 没有找到指定设备
    case deviceNotFound
    // 连接失败
    case connectionFailed
    // 没有找到指定服务
    case serviceNotFound
    // 没有找到指定特征值
    case characteristicIdNotFound
    // 当前连接已断开
    case disconnect
    // 当前特征值不支持此操作
    case unsupportedFunctions
    // 其余所有系统上报的异常
    case systemError
    // Android 系统特有，系统版本低于 4.3 不支持 BLE
    case bleUnavailable
    // 没有找到指定描述符
    case descriptorNotFound
    // 发送的数据为空或格式错误
    case invalidData
    // 操作超时
    case operationTimeout

    public var bizDomain: Int { 15 }
    public var funcDomain: Int { 1 }

    public var rawValue: Int {
        switch self {
        case .adapterUninitialized:
            return 1
        case .adapterUnavailable:
            return 2
        case .deviceNotFound:
            return 3
        case .connectionFailed:
            return 4
        case .serviceNotFound:
            return 5
        case .characteristicIdNotFound:
            return 6
        case .disconnect:
            return 7
        case .unsupportedFunctions:
            return 8
        case .systemError:
            return 9
        case .bleUnavailable:
            return 10
        case .descriptorNotFound:
            return 11
        case .invalidData:
            return 12
        case .operationTimeout:
            return 13
        }
    }
    
    public var errString: String {
        switch self {
        case .adapterUninitialized:
            return "Bluetooth adapter not initialized"
        case .adapterUnavailable:
            return "Bluetooth adapter not available"
        case .deviceNotFound:
            return "Device not found"
        case .connectionFailed:
            return "Connection failed"
        case .serviceNotFound:
            return "Service not found"
        case .characteristicIdNotFound:
            return "CharacteristicId not found"
        case .disconnect:
            return "Connection lost"
        case .unsupportedFunctions:
            return "Operation not available on this characteristic"
        case .systemError:
            return "System error"
        case .bleUnavailable:
            return "BLE not available on this device"
        case .descriptorNotFound:
            return "Descriptor not found"
        case .invalidData:
            return "Invalid data"
        case .operationTimeout:
            return "Operation time out"
        }
    }
}
