/// 聊天Errno
public enum OpenAPIChatErrno: Int, OpenAPIErrnoProtocol {
    // 数据异常.
    case NetworkDataException = 1
    // 取消
    case Cancel = 2
    // 打开机器人聊天失败，请确定小程序有开启机器人功能
    case NoBotId = 11
    // 参数错误. openId 和 openChatId 不能同时为空
    case OpenIdAndOpenChatIdCannotBothEmpty = 12
    // Mode 错误. 请切换为 appCenter 模式
    case modeError = 13

    public var bizDomain: Int { 10 }
    public var funcDomain: Int { 2 }
    
    public var errString: String {
        switch self {
        case .NetworkDataException:
            return "Data exception."
        case .Cancel:
            return "Cancel."
        case .NoBotId:
            return "Unable to launch a chat with the bot. Please check if the bot capability has been enabled for your app."
        case .OpenIdAndOpenChatIdCannotBothEmpty:
            return "Parameter error. openId and openChatId cannot be both empty."
        case .modeError:
            return "Wrong mode. Please switch to appCenter mode."
        }
    }
}
