/// 媒体 - 录制音频Errno
public enum OpenAPIRecorderErrno: OpenAPIErrnoProtocol {
    // 开始录制音频抢占播放失败(有优先级更高的业务方在播放)
    case startRecordHigherPriorityFailed(errorString: String)
    // 恢复录制音频抢占播放失败(有优先级更高的业务方在播放)
    case resumeRecordHigherPriorityFailed(errorString: String)
    // API 调用失败其他错误
    case recordOperateFailed(apiType: String, errString: String)

    public var bizDomain: Int { 13 }
    public var funcDomain: Int { 5 }

    public var rawValue: Int {
        switch self {
        case .startRecordHigherPriorityFailed:
            return 1
        case .resumeRecordHigherPriorityFailed:
            return 2
        case .recordOperateFailed:
            return 3
        }
    }
    
    public var errString: String {
        switch self {
        case .startRecordHigherPriorityFailed(let errorString):
            return "Unable to record. A higher priority media event is being processed, \(errorString)"
        case .resumeRecordHigherPriorityFailed(let errorString):
            return "Unable to record. A higher priority media event is being processed, \(errorString)"
        case .recordOperateFailed(let apiType, let errString):
            return "OperateRecorder \(apiType) failed, reason: \(errString)"
        }
    }
}
