#define BEF_LINK_BYTENN         0x00000001
#define BEF_LINK_AGFX           0x00000002
#define BEF_LINK_GAIA_LIB       0x00000004
#define BEF_LINK_AUDIO_EFFECT   0x00000008
#define BEF_LINK_SPEECH_SDK     0x00000010
#define BEF_LINK_IES_APPLOGGER  0x00000020
#define BEF_LINK_LENS           0x00000040
#define BEF_LINK_BORINGSSL      0x00000080
#define BEF_LINK_BYTEBENCH      0x00000100
/**
 * @brief   get effectsdk libs.
 * @return  Bits of libs.
 */
static inline long long bef_effect_get_sdk_libs()
{
    long long libs = BEF_LINK_BYTENN |
                     BEF_LINK_AGFX |
                     BEF_LINK_GAIA_LIB;

#ifdef BEF_FEATURE_CONFIG_AUDIO_EFFECT
    libs |= BEF_LINK_AUDIO_EFFECT;
#endif

#if defined(BEF_FEATURE_CONFIG_AUDIO_SPEECH_CAPT) || defined(BEF_FEATURE_CONFIG_AUDIO_SPEECH_ASR)
    libs |= BEF_LINK_SPEECH_SDK;
#endif

#ifdef BEF_USING_IESAPPLOG
    libs |= BEF_LINK_IES_APPLOGGER;
#endif

#ifdef BEF_ALGORITHM_CONFIG_VIDEO_SR
    libs |= BEF_LINK_LENS;
#endif

#ifdef BEF_FEATURE_CONFIG_AMAZING_BORINGSSL
    libs |= BEF_LINK_BORINGSSL;
#endif

#ifdef BEF_FEATURE_CONFIG_EFFECTGRADE
    libs |= BEF_LINK_BYTEBENCH;
#endif

    return libs;
}

